const API_KEY= "00dbfd406a0acaaecc568e7ea6a34ce5";
const BASE_URL="https://api.openweathermap.org/data/2.5";
 
 const getWeatherData = (infoType, seachParams) => {
    const url = new URL(BASE_URL + "/" + infoType);
    url.search = new URLSearchParams({...seachParams, appid: 
    API_KEY });

    return fetch(url)
    .then((res) => res.json())
 };

    const formatCurrentWeather = (data) => {
        const {
          coord: { lat, lon },
          main: { temp, feels_like, temp_min, temp_max, humidity,wind },
          name,
          dt,
          sys: { country, sunrise, sunset },
          weather,
          wind: { speed },
        } = data
        const {main: details, icon}=weather[0]

        return {
            wind,
            humidity,
            
            lat,
            lon,
            temp,
            feels_like,
            temp_min,
            temp_max,
            humidity,
            wind,
            name,
            dt,
            country,
            sunrise,
            sunset,
            details,
            icon,
            speed,
          };
 }
    
 


 const getFormattedWeatherData = async (searchParams) => {
    const formattedCurrentWeather = await getWeatherData
    ("weather", searchParams
    ).then( formattedCurrentWeather)
 }
