import './App.css';
import UilReact from '@iconscout/react-unicons/icons/uil-react'
import Inputs from './components/page-1/Inputs';
import Image from './components/page-1/Image';
import HourlyForecast from './components/page-1/HourlyForecast';
import TimeLoc from './components/page-1/TimeLoc';
import getWeatherData from './services/WeatherService';
import getFormattedWeatherData from './services/WeatherService';

function App() {
  const [query, setQuery] = useState({ q: "berlin" });
  const [units, setUnits] = useState("metric");
  const [weather, setWeather] = useState(null);
  const fetchWeather = async () => {
    const data = await getFormattedWeatherData({q: 'London'});
    console.log(data);
  }
  fetchWeather();

  return (
    <div className='mx-auto max-w-screen-md mt-4 py-5 px-32 bg-gradient-to-br from-slate-900 to-stone-800 h-fit shadow-xl shadown-gray-400 border-radius: 0.5rem; /* 8px */'>
      <Inputs/>
      <TimeLoc/>
      <Image/>
      <HourlyForecast/>
      </div>
  );
}

export default App;
